mapper.update:
UPDATE ASM_CORES

    name = #{name},
    location = #{location},
    template_engine = #{templateEngine}

WHERE id = #{id}

mapper.select:
SELECT *
FROM ASM_CORES

    id = #{id}
    AND location = #{location}
    AND name = #{name}
    AND template_engine = #{templateEngine}

mapper.update:
UPDATE ASMS
name = #{name}, type = #{type}, description = #{description}, asm_core_id = #{core.id,jdbcType=BIGINT}, template = #{template}
WHERE id = #{id}

UPDATE ASMS
SET asm_core_id = #{coreId,jdbcType=BIGINT}
WHERE id = #{id}

mapper.select:
SELECT asm.*, attr.id AS attr_id, attr.asm_id AS attr_asm_id, attr.name AS attr_name, attr.label AS attr_label, attr.type AS attr_type, attr.options AS attr_options, attr.value AS attr_value, attr.large_value AS attr_large_value, attr.ordinal AS attr_ordinal, attr.required AS attr_required, core.id AS core_id, core.location AS core_location, core.name AS core_name, core.template_engine AS core_template_engine FROM ASMS AS asm LEFT JOIN ASM_ATTRS attr ON attr.asm_id = asm.id LEFT JOIN ASM_CORES core ON core.id = asm.asm_core_id
asm.id = #{CQ_PK} asm.id = #{id} asm.name = #{name} AND asm.type = #{type} AND core.location = #{location}
ORDER BY ${CQ_ORDERBY}
LIMIT ${CQ_LIMIT}
OFFSET ${CQ_OFFSET}

mapper.update:
UPDATE ASMS
SET name = #{name}
WHERE id = #{id}

mapper.select:
SELECT
    COUNT(*)
FROM ASM_PARENTS AS p
WHERE p.asm_id = #{asmId}
      AND p.asm_parent_id = #{parentId}

mapper.delete:
DELETE
FROM ASM_PARENTS
WHERE asm_id = #{asmId} AND asm_parent_id = #{parentId}

mapper.select:
SELECT
    attr.*
FROM ASM_ATTRS attr
WHERE attr.asm_id = #{asm_id}
      AND attr.name = #{name}

mapper.update:
UPDATE ASMS
SET ordinal = SEQ_ASMS_ORDINAL.NEXTVAL
WHERE id = #{id}

mapper.select:
SELECT asm.id AS "id", asm.name AS "name", asm.hname AS "hname", asm.type AS "type", asm.cdate AS "cdate", asm.mdate AS "mdate", asm.nav_parent_id AS "nav_parent_id", attr.id AS "attr_id", attr.name AS "attr_name", attr.type AS "attr_type", attr.value AS "attr_value", iv.attr_id AS "iv_attr_id", iv.value AS "iv_value", npasm.id AS "np_id", npasm.hname AS "np_hname", npasm.name AS "np_name" FROM (SELECT DISTINCT asm.id, asm.ordinal FROM ASMS AS asm INNER JOIN ASM_ATTRS AS attr ON attr.asm_id = asm.id LEFT JOIN ASM_ATTR_IDXVALS AS iv1 ON iv1.attr_id = attr.id
LEFT JOIN ASM_PARENTS AS parent ON parent.asm_id = asm.id LEFT JOIN ASMS AS pasm ON parent.asm_parent_id = pasm.id
WHERE
AND asm.nav_parent_id = #{navParentId}
AND asm.published = #{published}
AND asm.type LIKE #{type}
AND pasm.name IN
#{t}
AND (
OR ( attr.type NOT IN ( 'select', 'date' ) AND attr.name = #{attr.one} AND attr.value LIKE #{attr.two} ) OR ( attr.type IN ( 'select' ) AND attr.name = #{attr.one} AND iv1.value LIKE #{attr.two} )
)
ORDER BY ${CQ_ORDERBY}
LIMIT ${CQ_LIMIT}
OFFSET ${CQ_OFFSET}
) AS restr(id, ordinal) INNER JOIN ASMS AS asm ON restr.id = asm.id LEFT JOIN ASM_ATTRS AS attr ON attr.asm_id = asm.id
AND attr.name IN #{an}
AND attr.name NOT IN #{an}
LEFT JOIN ASM_ATTR_IDXVALS AS iv ON iv.attr_id = attr.id LEFT JOIN ASMS AS npasm ON npasm.id = asm.nav_parent_id ORDER BY ${CQ_ORDERBY}
