mapper.select:
SELECT
    asm.id            AS "id",
    asm.name          AS "name",
    asm.hname         AS "hname",
    asm.description   AS "description",
    asm.type          AS "type",
    asm.published     AS "published",
    asm.template      AS "template"
FROM ASMS asm

    AND asm.type = #{type}
    AND (asm.name LIKE #{name} OR asm.description LIKE #{name})
    AND asm.template = #{template}
    AND asm.template_mode = #{template_mode}
    AND asm.name LIKE #{name_restriction}
    AND asm.id != #{exclude}

ORDER BY ${CQ_ORDERBY}

SELECT COUNT(*) FROM ASMS AS asm

    AND asm.type = #{type}
    AND (asm.name LIKE #{name} OR asm.description LIKE #{name})
    AND asm.template = #{template}
    AND asm.template_mode = #{template_mode}
    AND asm.name LIKE #{name_restriction}
    AND asm.id != #{exclude}

SELECT
    attr.*
FROM ASM_ATTRS attr
WHERE attr.asm_id = #{asm_id}
      AND attr.name = #{name}

mapper.update:
UPDATE ASM_ATTRS
SET name = #{new_name}
WHERE name = #{old_name}
      AND asm_id = #{asm_id}

mapper.delete:
DELETE FROM ASM_ATTRS
WHERE name = #{name}
      AND asm_id = #{asm_id}

DELETE FROM ASM_ATTRS
WHERE
    name = #{name}
    AND asm_id IN (
        SELECT
            ap.asm_id
        FROM ASM_PARENTS AS ap
        WHERE ap.asm_parent_id = #{parent_id}
    )

mapper.update:
UPDATE ASM_ATTRS
SET
    label       = #{label},
    type        = #{type},
    options     = #{options},
    value       = #{value},
    large_value = #{largeValue},
    required    = #{required}
WHERE asm_id = #{asmId}
      AND name = #{name}

UPDATE ASMS
template = #{template}, template_mode = #{template_mode}, description = #{description}, controller = #{controller},
WHERE id = #{id}
