mapper.update:
UPDATE ASMS
SET muser = #{muser,jdbcType=VARCHAR}, mdate = #{mdate,jdbcType=TIMESTAMP}
WHERE id = #{id}

MERGE INTO ASM_MEDIA_DEPS md USING (VALUES
(${r[0]}, ${r[1]})
) AS r(attr_id, file_id) ON md.attr_id = r.attr_id AND md.file_id = r.file_id WHEN NOT MATCHED THEN INSERT(attr_id, file_id) VALUES(r.attr_id, r.file_id) ELSE IGNORE

MERGE INTO ASM_PAGE_DEPS md USING (VALUES
(${r[0]}, '${r[1]}')
) AS r(attr_id, asm_name) ON md.attr_id = r.attr_id AND md.asm_name = r.asm_name WHEN NOT MATCHED AND EXISTS (SELECT 1 FROM ASMS a WHERE a.name = r.asm_name) THEN INSERT(attr_id, asm_name) VALUES(r.attr_id, r.asm_name) ELSE IGNORE
