mapper.select:
SELECT
    rule.id       AS "id",
    rule.name     AS "name",
    rule.ordinal  AS "ordinal",
    rule.cdate    AS "cdate",
    rule.mdate    AS "mdate",
    rule.flags    AS "flags"
FROM MTT_RULES rule

    LOWER(rule.name) LIKE #{name}

ORDER BY ${CQ_ORDERBY}

    LIMIT ${CQ_LIMIT}

        OFFSET ${CQ_OFFSET}

SELECT rule.id
FROM MTT_RULES rule
WHERE rule.name = #{name}

mapper.update:
UPDATE MTT_RULES
SET name = #{name}
WHERE id = #{id}

mapper.select:
SELECT
    filter.id           AS "id",
    filter.type         AS "type",
    filter.description  AS "description",
    filter.cdate        AS "cdate",
    filter.mdate        AS "mdate"
FROM MTT_FILTERS filter
WHERE filter.rule_id = #{rid}
ORDER BY ${CQ_ORDERBY}

    LIMIT ${CQ_LIMIT}

        OFFSET ${CQ_OFFSET}

SELECT COUNT(*)
FROM MTT_FILTERS filter
WHERE filter.rule_id = #{rid}
