mapper.select:
SELECT
rule.id AS "id",
rule.name AS "name",
rule.ordinal AS "ordinal",
rule.cdate AS "cdate",
rule.mdate AS "mdate",
rule.flags AS "flags"
FROM MTT_RULES rule

    LOWER(rule.name) LIKE #{name}

ORDER BY ${CQ_ORDERBY}

    LIMIT ${CQ_LIMIT}

        OFFSET ${CQ_OFFSET}

SELECT rule.id
FROM MTT_RULES rule
WHERE rule.name = #{name}

mapper.update:
UPDATE MTT_RULES
SET name = #{name}, mdate = CURRENT_TIMESTAMP
WHERE id = #{id}

UPDATE MTT_RULES
SET flags = #{flags}, mdate = CURRENT_TIMESTAMP
WHERE id = #{id}

UPDATE MTT_RULES
SET ordinal = #{newOrdinal}, mdate = CURRENT_TIMESTAMP

    ordinal = #{ordinal}
    id= #{id}

UPDATE MTT_RULES
SET enabled = #{enabled}, mdate = CURRENT_TIMESTAMP
WHERE id = #{id}

mapper.select:
SELECT
filter.id AS "id",
filter.rule_id AS "ruleId",
filter.type AS "type",
filter.description AS "description",
filter.cdate AS "cdate",
filter.mdate AS "mdate"
FROM MTT_FILTERS filter
WHERE filter.rule_id = #{rid}
ORDER BY ${CQ_ORDERBY}

    LIMIT ${CQ_LIMIT}

        OFFSET ${CQ_OFFSET}

SELECT COUNT(*)
FROM MTT_FILTERS filter
WHERE filter.rule_id = #{rid}

mapper.update:
UPDATE MTT_FILTERS
SET description = #{description}, spec = #{spec}, mdate = CURRENT_TIMESTAMP
WHERE id = #{id}

mapper.select:
