action.type AS "type",
action.ordinal AS "ordinal",
action.description AS "description",
action.cdate AS "cdate",
action.mdate AS "mdate"
FROM MTT_ACTIONS action
WHERE action.rule_id = #{rid}
ORDER BY ${CQ_ORDERBY}

    LIMIT ${CQ_LIMIT}

        OFFSET ${CQ_OFFSET}

SELECT COUNT(*)
FROM MTT_ACTIONS action
WHERE action.rule_id = #{rid}

mapper.update:
UPDATE MTT_ACTIONS
SET ordinal = #{newOrdinal}, mdate = CURRENT_TIMESTAMP

    ordinal = #{ordinal}
    id= #{id}
