mapper.update:
UPDATE asm_cores

    name = #{name},
    location = #{location},
    template_engine = #{templateEngine}

WHERE id = #{id}

mapper.select:
SELECT *
FROM asm_cores

    id = #{id}
    AND location = #{location}
    AND name = #{name}
    AND template_engine = #{templateEngine}

mapper.update:
UPDATE asms

    name = #{name},
    type = #{type},
    description = #{description},
    asm_core_id = #{core.id,jdbcType=BIGINT}

WHERE id = #{id}

UPDATE asms
SET asm_core_id = #{coreId,jdbcType=BIGINT}
WHERE id = #{id}

mapper.select:
SELECT asm.*,
attr.asm_id AS attr_asm_id,
attr.name AS attr_name,
attr.type AS attr_type,
attr.options AS attr_options,
attr.value AS attr_value,
attr.large_value AS attr_large_value,
core.id AS core_id,
core.location AS core_location,
core.name AS core_name,
core.template_engine AS core_template_engine
FROM asms AS asm
LEFT OUTER JOIN asm_attrs attr ON attr.asm_id = asm.id
LEFT OUTER JOIN asm_cores core ON core.id = asm.asm_core_id

    asm.id = #{CQ_PK}
    asm.id = #{id}
    asm.name = #{name}
    AND asm.type = #{type}
    AND core.location = #{location}

ORDER BY ${CQ_ORDERBY}

    LIMIT ${CQ_LIMIT}

        OFFSET ${CQ_OFFSET}

mapper.update:
UPDATE asms
SET name = #{name}
WHERE id = #{id}

mapper.select:
SELECT
    count(*)
FROM asm_parents AS p
WHERE p.asm_id = #{asmId}
      AND p.asm_parent_id = #{parentId}

mapper.delete:
DELETE
FROM asm_parents
WHERE asm_id = #{asmId} AND asm_parent_id = #{parentId}

mapper.select:
SELECT
    attr.*
FROM asm_attrs attr
WHERE attr.asm_id = #{asm_id}
      AND attr.name = #{name}
