mapper.select:
SELECT
asm.id AS "id",
asm.name AS "name",
asm.description AS "description",
asm.type AS "type",
asm.published AS "published",
asm.template AS "template"
FROM asms asm

    AND asm.type = #{type}
    AND asm.name LIKE #{name}
    AND asm.template = #{template}

ORDER BY ${CQ_ORDERBY}

SELECT COUNT(*) FROM asms AS asm

    AND asm.type = #{type}
    AND asm.name LIKE #{name}
    AND asm.template = #{template}

SELECT
    attr.*
FROM asm_attrs attr
WHERE attr.asm_id = #{asm_id}
      AND attr.name = #{name}

mapper.update:
UPDATE asm_attrs
SET name = #{new_name}
WHERE name = #{old_name}
      AND asm_id = #{asm_id}

mapper.delete:
DELETE FROM asm_attrs
WHERE name = #{name}
      AND asm_id = #{asm_id}

mapper.update:
UPDATE asm_attrs
SET
    label       = #{label},
    type        = #{type},
    options     = #{options},
    value       = #{value},
    large_value = #{largeValue},
    required    = #{required}
WHERE asm_id = #{asmId}
      AND name = #{name}

UPDATE asms
template = #{template}, description = #{description},
WHERE id = #{id}
