mapper.update:
UPDATE asms
SET owner = #{owner}
WHERE id = #{id}

UPDATE asm_attrs
SET
    options     = #{options},
    value       = #{value},
    large_value = #{largeValue}
WHERE asm_id = #{asmId}
      AND name = #{name}

mapper.delete:
DELETE FROM asm_attrs
WHERE asm_id = #{asmId}
AND name IN

    #{name}
