mapper.select:
select id
from media_entity
where folder = #{folder} and name = #{name}

mapper.update:
update media_entity e
set e.folder = #{nfolder},
    e.name = #{nname},
    mdate = now()
where e.folder = #{folder}
and e.name = #{name}

mapper.delete:
delete from media_entity e
where e.folder = #{folder}
and e.name = #{name}

mapper.select:
select
e.id as "id",
e.name as "name",
e.status as "status",
e.content_type as "content_type",
e.content_length as "content_length",
e.folder as "folder"
from media_entity e

    and e.folder like #{folder}
    and lower(e.name) like #{name}
    and e.status = #{status}

order by ${CQ_ORDERBY}

select
count(*)
from media_entity e

    and e.folder like #{folder}
    and lower(e.name) like #{name}
    and e.status = #{status}
