mapper.update:
UPDATE media_entity e
SET e.folder = #{nfolder},
    e.name   = #{nname},
    mdate    = now()
WHERE e.folder = #{folder}
      AND e.name = #{name}

mapper.delete:
DELETE FROM media_entity e
WHERE e.folder = #{folder}
      AND e.name = #{name}

mapper.select:
SELECT
e.id AS "id",
e.name AS "name",
e.status AS "status",
e.content_type AS "content_type",
e.content_length AS "content_length",
e.folder AS "folder",
e.creator AS "creator",
e.description AS "description",
e.tags AS "tags"
FROM media_entity e

    AND e.folder LIKE #{folder}
    AND LOWER(e.name) LIKE #{name}
    AND e.status = #{status}

order by ${CQ_ORDERBY}

    LIMIT ${CQ_LIMIT}

        OFFSET ${CQ_OFFSET}

SELECT
COUNT(*)
FROM media_entity e

    AND e.folder LIKE #{folder}
    AND LOWER(e.name) LIKE #{name}
    AND e.status = #{status}

SELECT
e.id AS "id",
e.name AS "name",
e.status AS "status",
e.content_type AS "content_type",
e.content_length AS "content_length",
e.folder AS "folder",
e.creator AS "creator",
e.description AS "description",
e.tags AS "tags"
FROM media_entity e, media_entity_kwords kw
WHERE
kw.entity_id = e.id
AND e.status = #{status}
AND e.folder LIKE #{folder}
AND

    kw.keyword LIKE #{k}

GROUP BY e.id
HAVING COUNT(e.id) = #{keywordsSize}
order by ${CQ_ORDERBY}

    LIMIT ${CQ_LIMIT}

        OFFSET ${CQ_OFFSET}

SELECT COUNT(*)
FROM (
SELECT e.id
FROM media_entity e, media_entity_kwords kw
WHERE
kw.ENTITY_ID = e.id
AND e.status = #{status}
AND e.folder LIKE #{folder}
AND

    kw.keyword LIKE #{k}

GROUP BY e.id
HAVING COUNT(e.id) = #{keywordsSize}
)

mapper.update:
UPDATE media_entity
SET
    icon              = #{icon,jdbcType=BLOB},
    icon_content_type = #{icon_content_type}
WHERE id = #{id}

UPDATE media_entity
tags = #{tags,jdbcType=ARRAY}, description = #{description},
WHERE id = #{id}

UPDATE media_entity
SET
    sys = #{sys}
WHERE id = #{id}
